func GetMarkup(files []ai.File) *tele.ReplyMarkup {
	markup := &tele.ReplyMarkup{}
	for _, f := range files {
		if url, ok := linkMap[f.Name]; ok {
			markup.Inline(markup.Row(markup.URL(f.Name, url)))
		}
	}
	return markup
}
